import React, { useState } from 'react';
import {
  Link
} from "react-router-dom";
import { useQuery } from '@apollo/client';

import {
  TextField, Button,
} from '@material-ui/core';

import '../style.css'
import Table from './Table';

import { getBikeById } from '../graphql/query/index'

export default function Bike(props) {
  const id = props.match.params.id;

  const { data: { bikes = {} } = {}, loading, error } = useQuery(getBikeById, { variables: { id } });

  // search field value
  const [input, setInput] = useState('');

  if (loading) return <p>Loading</p>;
  if (error) return <p>ERROR</p>;
  // if bike not exist, return the search bar without data showing 'bike not found' reminder
  if (!data.bike) return (
    <div>
      <form noValidate autoComplete="off" align='center'>
        <TextField
          id="standard-basic-1"
          label='bike not found'
          value={input}
          onChange={e => setInput(e.target.value)}
        />
        <label htmlFor="contained-button-file">
          <Link to={`/${input}`}>
            <Button variant="contained" color="primary">
              Search
            </Button>
          </Link>
        </label>
      </form >
    </div>
  );

  return (
    <div>
      <form noValidate autoComplete="off" align='center'>
        <TextField
          id="standard-basic"
          label="Search by Bike id"
          value={input}
          onChange={e => setInput(e.target.value)}
        />
        <label htmlFor="contained-button-file">
          <Link to={`/${input}`}>
            <Button variant="contained" color="primary">
              Search
            </Button>
          </Link>
        </label>
      </form >
      <Table bikes={[data.bike]} />

    </div>
  );
}
