              <TableCell align="right">Type</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {bikes.map(({ bike_id, lat, lon, is_disabled, is_reserved, vehicle_type }) => (
              <TableRow key={bike_id}>
                <Link to={`/${bike_id}`}>
